const replyText = $json.message.reply_to_message?.text || '';
const input = ($json.message.text || '').trim();

// 1) booking: сначала из replyText ("букинга XXX"), иначе из скобок в input "(XXX)"
let booking = '';
const mBookingFromReply = replyText.match(/букинга\s+([^\s)]+)/i);
if (mBookingFromReply) booking = mBookingFromReply[1];

if (!booking) {
  const mBookingFromInput = input.match(/\(\s*([A-Za-z0-9]+)\s*\)/);
  if (mBookingFromInput) booking = mBookingFromInput[1];
}

// 2) update_type: сначала по replyText (если есть), иначе по типу введённых данных
let updateType = '';
if (/название\s+судна/i.test(replyText)) updateType = 'Vessel';
else if (/ата/i.test(replyText)) updateType = 'ATA';
else if (/ета/i.test(replyText)) updateType = 'ETA';

// 3) row_number из callback_data (если это ответ на сообщение с кнопкой)
const row = $json.message.reply_to_message?.reply_markup?.inline_keyboard?.[0]?.[0]?.callback_data
  ?.split('|')?.[2] || '';

// 4) валидация даты (ATA/ETA): формат + дата >= сегодня
const dateRegex = /^\d{4}-(0[1-9]|1[0-2])-(0[1-9]|[12][0-9]|3[01])$/;

function dateOnlyUTC(d) {
  return new Date(Date.UTC(d.getUTCFullYear(), d.getUTCMonth(), d.getUTCDate()));
}

function parseYYYYMMDDtoUTC(s) {
  const [y, m, d] = s.split('-').map(n => parseInt(n, 10));
  return new Date(Date.UTC(y, m - 1, d));
}

let valid_date = false;

// Если replyText пустой и человек прислал НЕ дату — считаем это обновление Vessel
if (!updateType) {
  updateType = dateRegex.test(input) ? '' : 'Vessel';
}

if (updateType === 'Vessel') {
  valid_date = true; // для Vessel всегда пропускаем
} else if (updateType === 'ATA' || updateType === 'ETA') {
  const isFormatValid = dateRegex.test(input);

  if (isFormatValid) {
    const inputDate = parseYYYYMMDDtoUTC(input);
    const today = dateOnlyUTC(new Date());

    const y = inputDate.getUTCFullYear();
    const m = inputDate.getUTCMonth() + 1;
    const d = inputDate.getUTCDate();
    const reconstructed = `${String(y).padStart(4,'0')}-${String(m).padStart(2,'0')}-${String(d).padStart(2,'0')}`;
    const isRealDate = reconstructed === input;

    valid_date = isRealDate && (inputDate.getTime() >= today.getTime());
  } else {
    valid_date = false;
  }
}

// (опционально) vessel_name отдельно
const vessel_name = input.replace(/\(\s*[A-Za-z0-9]+\s*\)/, '').trim();

return [{
  json: {
    chat_id: $json.message.chat.id,
    date: input,            // для ATA/ETA это дата, для Vessel это строка
    vessel_name,            // удобно для Vessel
    booking,
    row_number: row,
    update_type: updateType,
    valid_date,
  }
}];

const raw = $json.callback_query?.data || '';
const [row_number, booking, data, line, quantity, type_update] = raw.split('|');

return [{
  json: {
    row_number: row_number || '',
    booking: booking || '',
    data: data || '',
    line: line || '',
    quantity: quantity || '', 
    type_update: type_update || '',
    chat_id: $json.callback_query?.from?.id || $json.callback_query?.message?.chat?.id || '',
    button_message_id: $json.callback_query?.message?.message_id || ''
  }
}];

