const tz = 'Europe/Kyiv';
const text = (
  $json.result?.text ||
  $json.message?.text ||
  $json.text ||
  ''
).toString();

const m = text.match(/ETA:\s*(\d{4}-(0[1-9]|1[0-2])-(0[1-9]|[12][0-9]|3[01]))/i);
const etaStr = m ? m[1] : '';

let etaDueSoon = false;
let diffDays = null;

if (etaStr) {
  const [y, mth, d] = etaStr.split('-').map(Number);
  const etaUtc = Date.UTC(y, mth - 1, d);
  const todayStr = new Date().toLocaleDateString('en-CA', { timeZone: tz });
  const [ty, tm, td] = todayStr.split('-').map(Number);
  const todayUtc = Date.UTC(ty, tm - 1, td);

  diffDays = (etaUtc - todayUtc) / 86400000;
  etaDueSoon = diffDays >= 0 && diffDays <= 7;
}

return [{
  json: {
    ...$json,
    diffDays,
    etaDueSoon
  }
}];

// n8n Code node (JavaScript)
// Supports inputs:
// 1) items[0].json.data = [ { chatId, text, reply_markup: { inline_keyboard: [...] }, ... }, ... ]
// 2) items[0].json.thisFieldAcceptsAnyType = [ { ok:true, result:{ chat:{id}, text, reply_markup } }, ... ]
// Output: many items: { chatId, text, inlineKeyboard }

function tryParseJson(s) {
  if (typeof s !== 'string') return null;
  try { return JSON.parse(s); } catch { return null; }
}

function getPayload(json) {
  // NEW: data array
  if (Array.isArray(json?.data)) return json.data;
  const parsedData = tryParseJson(json?.data);
  if (Array.isArray(parsedData)) return parsedData;

  // OLD: thisFieldAcceptsAnyType array
  if (Array.isArray(json?.thisFieldAcceptsAnyType)) return json.thisFieldAcceptsAnyType;
  const parsedAny = tryParseJson(json?.thisFieldAcceptsAnyType);
  if (Array.isArray(parsedAny)) return parsedAny;

  // fallback: if workflow passed multiple items directly
  if (Array.isArray(items) && items.length > 1) return items.map(i => i.json);

  return [];
}

function normalizeChatId(v) {
  const s = String(v ?? '').trim();
  if (!s) return null;
  // Telegram chat id обычно число, но можно оставить строкой
  return s;
}

function telegramMarkupToN8nInlineKeyboard(replyMarkup) {
  const ik = replyMarkup?.inline_keyboard;
  if (!Array.isArray(ik) || !ik.length) return null;

  return {
    rows: ik.map(rowArr => ({
      row: {
        buttons: (Array.isArray(rowArr) ? rowArr : []).map(btn => ({
          text: String(btn?.text ?? ''),
          additionalFields: {
            callback_data: String(btn?.callback_data ?? ''),
          },
        })).filter(b => b.text),
      },
    })),
  };
}

const root = items?.[0]?.json ?? {};
const arr = getPayload(root);

// default keyboard (если вдруг reply_markup нет)
const defaultInlineKeyboard = {
  rows: [
    {
      row: {
        buttons: [
          { text: 'Accept', additionalFields: { callback_data: 'accept' } },
          { text: 'Cancel', additionalFields: { callback_data: 'cancel' } },
        ],
      },
    },
  ],
};

const out = [];

for (const entry of arr) {
  // поддержка старого формата { ok, result: {...} }
  const src = entry?.result ? entry.result : entry;

  const chatId = normalizeChatId(src?.chat?.id ?? src?.chatId);
  const text = String(src?.text ?? '').trim();

  if (!chatId || !text) continue;

  const inlineKeyboard =
    telegramMarkupToN8nInlineKeyboard(src?.reply_markup) || defaultInlineKeyboard;

  out.push({
    json: {
      chatId,
      text,
      inlineKeyboard,
    },
  });
}

return out.length
  ? out
  : [{ json: { error: 'No valid items in input (need chatId + text)' } }];

const ifItem = ($items('If start from allowed user1', 0, 0) || [])[0];
const text = String(ifItem?.json?.callback_query?.message?.text ?? '').trim();

const m = text.match(/#\s*(\d+)/);

return [
  {
    json: {
      id_after_hash: m ? Number(m[1]) : null,
    },
  },
];

const ifItem = ($items('Telegram Callback', 0, 0) || [])[0];
const text = String(ifItem?.json?.callback_query?.message?.text ?? '').trim();

const m = text.match(/#\s*(\d+)/);

return [
  {
    json: {
      id_after_hash: m ? Number(m[1]) : null,
    },
  },
];

const day = new Date().getDay(); // 0=вс, 6=сб
if (day === 0 || day === 6) return []; // прекращаем выполнение
return items;
