const text = $json.message.reply_to_message?.text || '';
const input = $json.message.text || '';

// букинг из текста вопроса
const mBooking = text.match(/букинга\s+([^\s)]+)/i);
const booking = mBooking ? mBooking[1] : '';

// тип обновления по вопросу
let updateType = /ата/i.test(text) ? 'ATA' : (/ета/i.test(text) ? 'ETA' : '');

// Vessel-запрос: "Добавте новое название судна для букинга ..."
const vesselMatch = text.match(/название\s+судна.*букинга\s+([^\s)]+)/i);
if (vesselMatch) {
  updateType = 'Vessel';
}

// row_number из callback_data
const row = $json.message.reply_to_message?.reply_markup?.inline_keyboard?.[0]?.[0]?.callback_data
  ?.split('|')?.[2] || '';

// валидация даты для ATA/ETA
const dateRegex = /^\d{4}-(0[1-9]|1[0-2])-(0[1-9]|[12][0-9]|3[01])$/;
const isDateValid = dateRegex.test(input);
let valid_date = false;

if (updateType === 'Vessel') {
  // для Vessel пропускаем без проверки даты
  valid_date = true;
} else if (updateType === 'ATA' || updateType === 'ETA') {
  valid_date = isDateValid;
}

return [{
  json: {
    chat_id: $json.message.chat.id,
    date: input,          // дата или текст (для Vessel – новое название судна)
    booking,
    row_number: row,
    update_type: updateType,
    valid_date
  }
}];

const raw = $json.callback_query?.data || '';
const [row_number, booking, data, line, quantity, type_update] = raw.split('|');

return [{
  json: {
    row_number: row_number || '',
    booking: booking || '',
    data: data || '',
    line: line || '',
    quantity: quantity || '', 
    type_update: type_update || '',
    chat_id: $json.callback_query?.from?.id || $json.callback_query?.message?.chat?.id || '',
    button_message_id: $json.callback_query?.message?.message_id || ''
  }
}];

