const tz = 'Europe/Kyiv';
const text = (
  $json.result?.text ||
  $json.message?.text ||
  $json.text ||
  ''
).toString();

const m = text.match(/ETA:\s*(\d{4}-(0[1-9]|1[0-2])-(0[1-9]|[12][0-9]|3[01]))/i);
const etaStr = m ? m[1] : '';

let etaDueSoon = false;
let diffDays = null;

if (etaStr) {
  const [y, mth, d] = etaStr.split('-').map(Number);
  const etaUtc = Date.UTC(y, mth - 1, d);
  const todayStr = new Date().toLocaleDateString('en-CA', { timeZone: tz });
  const [ty, tm, td] = todayStr.split('-').map(Number);
  const todayUtc = Date.UTC(ty, tm - 1, td);

  diffDays = (etaUtc - todayUtc) / 86400000;
  etaDueSoon = diffDays >= 0 && diffDays <= 7;
}

return [{
  json: {
    ...$json,
    diffDays,
    etaDueSoon
  }
}];

const day = new Date().getDay(); // 0=вс, 6=сб
if (day === 0 || day === 6) return []; // прекращаем выполнение
return items;

// n8n Code node (JavaScript)
// Input: items[0].json.thisFieldAcceptsAnyType = Array of { ok, result: { chat:{id}, text, ... } }
// Output: many items: { chatId, text, inlineKeyboard }

function getPayload(json) {
  // основной кейс
  if (Array.isArray(json?.thisFieldAcceptsAnyType)) return json.thisFieldAcceptsAnyType;

  // иногда это может быть строкой JSON (если где-то включили convertFieldsToString)
  if (typeof json?.thisFieldAcceptsAnyType === 'string') {
    try {
      const parsed = JSON.parse(json.thisFieldAcceptsAnyType);
      if (Array.isArray(parsed)) return parsed;
    } catch (e) {}
  }

  // запасные варианты, если поле называется иначе
  if (Array.isArray(json?.data)) return json.data;
  if (typeof json?.data === 'string') {
    try {
      const parsed = JSON.parse(json.data);
      if (Array.isArray(parsed)) return parsed;
    } catch (e) {}
  }

  return [];
}

const root = items?.[0]?.json ?? {};
const arr = getPayload(root);

const inlineKeyboard = {
  rows: [
    {
      row: {
        buttons: [
          { text: 'Accept', additionalFields: { callback_data: 'in progress' } },
          { text: 'Cancel', additionalFields: { callback_data: 'cancel' } },
        ],
      },
    },
  ],
};

const out = [];

for (const entry of arr) {
  const res = entry?.result ?? {};
  const chatId = res?.chat?.id;
  const text = res?.text;

  if (!chatId || !text) continue;

  out.push({
    json: {
      chatId,
      text,
      inlineKeyboard,
    },
  });
}

return out.length ? out : [{ json: { error: 'No valid telegram results in thisFieldAcceptsAnyType' } }];
