function parseDateFlexible(s) {
  if (!s && s !== 0) return null;
  s = String(s).trim();
  if (!s) return null;

  // Попробуем распознать форматы типа 07.05.2019 или 7/5/2019 или 07-05-2019
  const dmy = s.match(/^(\d{1,2})[.\-\/](\d{1,2})[.\-\/](\d{2,4})$/);
  if (dmy) {
    let day = parseInt(dmy[1], 10);
    let month = parseInt(dmy[2], 10);
    let year = parseInt(dmy[3], 10);
    if (year < 100) { // two-digit year heuristic
      year += year >= 70 ? 1900 : 2000;
    }
    // Validate
    const dateObj = new Date(year, month - 1, day);
    if (!isNaN(dateObj)) return dateObj;
  }

  // Попробуем ISO или встроенный парсер
  const iso = Date.parse(s);
  if (!isNaN(iso)) return new Date(iso);

  return null;
}

// Возвращает строку с ведущими нулями согласно вашим правилам (только нули)
function zeroPrefixForId(id) {
  if (id === undefined || id === null || id === '') return '';
  const str = String(id).trim();
  const n = Number(str);
  if (Number.isNaN(n)) return '';
  if (n >= 0 && n <= 9) return '0000';
  if (n <= 99) return '000';
  if (n <= 999) return '00';
  if (n <= 9999) return '0';
  return '';
}

// Паддинг id: возвращает строку с нулями + id, либо просто id если нулей не требуется
function padIdString(id) {
  const z = zeroPrefixForId(id);
  if (z === '') return String(id === undefined || id === null ? '' : String(id).trim());
  return z + String(id).trim();
}

// Сравнение двух записей: возвращает true если a is newer than b
function isANewerThanB(a, b) {
  const da = parseDateFlexible(a.agreement_date);
  const db = parseDateFlexible(b.agreement_date);

  if (da && db) {
    if (da.getTime() !== db.getTime()) return da.getTime() > db.getTime();
    // даты равны -> tie-breaker: сравниваем padded-строки
    const padA = padIdString(a.id);
    const padB = padIdString(b.id);
    if (padA === padB) return false; // при равенстве не заменяем
    return padA > padB;
  }

  // если только одна из дат есть — предпочитаем существующую дату (новее)
  if (da && !db) return true;
  if (!da && db) return false;

  // обе даты отсутствуют — tie-breaker по padded id
  const padA2 = padIdString(a.id);
  const padB2 = padIdString(b.id);
  if (padA2 === padB2) return false;
  return padA2 > padB2;
}

// Основная логика
const mapByCode = new Map();

// Поддерживаем вход как массив items (каждый item.json — объект)
for (const item of items) {
  const obj = item.json ?? {};
  const code = String(obj.customer_code ?? '').trim();
  if (!code) {
    // если нет customer_code — пропускаем (можно менять политику)
    continue;
  }

  if (!mapByCode.has(code)) {
    mapByCode.set(code, obj);
  } else {
    const existing = mapByCode.get(code);
    if (isANewerThanB(obj, existing)) {
      mapByCode.set(code, obj);
    }
  }
}

// Собираем результат в формате n8n items, добавляя ключ `zero` — строка нулей по правилам
const output = [];
for (const kept of mapByCode.values()) {
  const zero = zeroPrefixForId(kept.id);
  output.push({ json: { ...kept, zero } });
}

return output;
