// Execution Mode = runOnceForEachItem
try {
  // item — текущий вход
  const result = {
    pageNumber: item.json.pageNumber ?? null,
    vision: item.json
  };
  return { json: result }; // возвращаем ТОЛЬКО один объект (не массив)
} catch (err) {
  return { json: { _error: String(err) } };
}

const outputs = [];
for (const item of items) {
  const images = item.json?.images ?? item.json?.result?.images ?? [];

  if (!Array.isArray(images) || images.length === 0) {
    outputs.push({ json: { _error: 'PDF conversion returned no images', originalJsonKeys: Object.keys(item.json ?? {}) } });
    continue;
  }
  for (const image of images) {
    let base64 = null;

    if (typeof image.base64 === 'string' && image.base64.length > 0) {
      base64 = image.base64;
    } else if (typeof image.data === 'string' && image.data.length > 0) {
      base64 = image.data;
    } else if (image?.binary && typeof image.binary === 'object') {
      try {
        if (image.binary.data && Buffer.isBuffer(image.binary.data)) {
          base64 = image.binary.data.toString('base64');
        } else if (typeof image.binary.data === 'string') {
          base64 = image.binary.data;
        }
      } catch (e) { /* ignore */ }
    } 
    if (!base64) {
      outputs.push({ json: { _warning: 'image has no base64/data', pageNumber: image.pageNumber ?? image.page ?? null, originalImageKeys: Object.keys(image ?? {}) } });
      continue;
    }

    outputs.push({ json: { base64, pageNumber: image.pageNumber ?? image.page ?? null, mimeType: image.mimeType || 'image/jpeg' } });
  }
}
return outputs;

return (() => {
  // Ищем pages[] в любом входном элементе
  let pages = [];
  for (const it of $input.all()) {
    const p = it.json?.result?.pages;
    if (Array.isArray(p) && p.length) {
      pages = p;
      break;
    }
  }

  // Если pages нет — вернём пустой массив (или включите "Always Output Data" в настройках узла)
  if (!pages.length) {
    return [];
  }

  // Возвращаем массив { json: { text } } по количеству страниц
  return pages.map(p => ({
    json: {
      text: String(p?.text ?? ''),
    },
  }));
})();

function normalize(input) {
  // Вход может быть: {result:{text,pages...}}, или [{...}], или сразу {text,pages}
  let root = input;
  if (Array.isArray(root) && root.length > 0) root = root[0];

  // Если это конверт "job/status/result"
  if (root && root.result && (typeof root.result === 'object')) {
    return {
      jobId: root.jobId ?? null,
      status: root.status ?? null,
      fileName: root.result.fileName ?? null,
      text: typeof root.result.text === 'string' ? root.result.text : '',
      pages: Array.isArray(root.result.pages) ? root.result.pages : [],
    };
  }

  // Если пришёл уже плоский объект { text, pages }
  return {
    jobId: root.jobId ?? null,
    status: root.status ?? null,
    fileName: root.fileName ?? null,
    text: typeof root.text === 'string' ? root.text : '',
    pages: Array.isArray(root.pages) ? root.pages : [],
  };
}

function cleanText(s) {
  if (!s) return '';
  return String(s).replace(/\r/g, '').trim();
}

// Пробегаем по всем входным items и нормализуем в единый формат
const out = [];
for (const item of items) {
  const n = normalize(item.json);
  const text = cleanText(n.text);
  const pages = Array.isArray(n.pages)
    ? n.pages.map((p) => ({
        pageNumber: p.pageNumber ?? null,
        text: cleanText(p.text ?? ''),
        textLength: typeof p.text === 'string' ? p.text.length : 0,
        imageFile: p.imageFile ?? null,
        error: p.error ?? undefined,
      }))
    : [];

  out.push({
    json: {
      success: true,
      source: 'ocr',
      jobId: n.jobId,
      status: n.status,
      fileName: n.fileName,
      text,
      pages,
      pageCount: pages.length,
    },
  });
}
return out;

const out = [];

for (const item of items) {
  const pages = Array.isArray(item.json?.pages) ? item.json.pages : [];

  if (pages.length === 0) {
    const t = item.json?.text ?? '';
    out.push({
      json: {
        pageNumber: 1,
        text: t,
        textLength: typeof t === 'string' ? t.length : 0,
        jobId: item.json?.jobId,
        fileName: item.json?.fileName,
      },
    });
    continue;
  }

  let i = 0;
  for (const p of pages) {
    i += 1;
    const t = p?.text ?? '';
    out.push({
      json: {
        pageNumber: p?.pageNumber ?? i,
        text: t,
        textLength: typeof t === 'string' ? t.length : 0,
        jobId: item.json?.jobId,
        fileName: item.json?.fileName,
      },
    });
  }
}

return out;
