return (() => {
  // Собираем все входные айтемы
  const all = $input.all();

  // Пытаемся найти pages в любом элементе: it.json.result.pages
  let pages = [];
  for (const it of all) {
    const p = it.json?.result?.pages;
    if (Array.isArray(p) && p.length) {
      pages = p;
      break;
    }
  }

  // Если pages не нашли, попробуем собрать страницы из полей text/pageText
  if (!pages.length) {
    const candidates = [];
    for (const it of all) {
      const t =
        it.json?.text ??
        it.json?.pageText ??
        it.json?.result?.text ??
        null;
      if (typeof t === 'string' && t.trim().length) {
        candidates.push(t);
      }
    }
    if (candidates.length) {
      pages = candidates.map((t, i) => ({ index: i + 1, text: t }));
    }
  }

  // Если всё ещё ничего — вернём пусто (или включите "Always Output Data")
  if (!pages.length) {
    return [];
  }

  // Нормализация структуры и текста
  const normalize = (s) => String(s || '')
    .replace(/\u00A0/g, ' ')      // NBSP -> space
    .replace(/[ \t]+/g, ' ')      // сжать подряд идущие пробелы/табы
    .replace(/\r/g, '')           // убрать CR
    .replace(/\n{3,}/g, '\n\n')   // сжать лишние пустые строки
    .trim();

  // Приводим любой формат pages к [{ index, text }]
  pages = pages.map((p, i) => {
    const text = typeof p === 'string' ? p : (p?.text ?? '');
    const index = (typeof p === 'object' && Number.isInteger(p?.index)) ? p.index : (i + 1);
    return { index, text: normalize(text) };
  })
  // Убираем полностью пустые страницы после нормализации
  .filter(p => p.text.length > 0);

  // Опционально — ограничение длины страницы, чтобы не взрывать токены
  const MAX_CHARS_PER_PAGE = 20000;
  pages = pages.map(p => {
    if (p.text.length > MAX_CHARS_PER_PAGE) {
      return { ...p, text: p.text.slice(0, MAX_CHARS_PER_PAGE) + '\n…[truncated]' };
    }
    return p;
  });

  const pageCount = pages.length;

  // Возвращаем ОДИН айтем для LLM (исключает мультизапуск)
  return [{
    json: {
      pages,
      pageCount
    }
  }];
})();

return (() => {
  // Ищем pages[] в любом входном элементе
  let pages = [];
  for (const it of $input.all()) {
    const p = it.json?.result?.pages;
    if (Array.isArray(p) && p.length) {
      pages = p;
      break;
    }
  }

  // Если pages нет — вернём пустой массив (или включите "Always Output Data" в настройках узла)
  if (!pages.length) {
    return [];
  }

  // Возвращаем массив { json: { text } } по количеству страниц
  return pages.map(p => ({
    json: {
      text: String(p?.text ?? ''),
    },
  }));
})();

function normalize(input) {
  // Вход может быть: {result:{text,pages...}}, или [{...}], или сразу {text,pages}
  let root = input;
  if (Array.isArray(root) && root.length > 0) root = root[0];

  // Если это конверт "job/status/result"
  if (root && root.result && (typeof root.result === 'object')) {
    return {
      jobId: root.jobId ?? null,
      status: root.status ?? null,
      fileName: root.result.fileName ?? null,
      text: typeof root.result.text === 'string' ? root.result.text : '',
      pages: Array.isArray(root.result.pages) ? root.result.pages : [],
    };
  }

  // Если пришёл уже плоский объект { text, pages }
  return {
    jobId: root.jobId ?? null,
    status: root.status ?? null,
    fileName: root.fileName ?? null,
    text: typeof root.text === 'string' ? root.text : '',
    pages: Array.isArray(root.pages) ? root.pages : [],
  };
}

function cleanText(s) {
  if (!s) return '';
  return String(s).replace(/\r/g, '').trim();
}

// Пробегаем по всем входным items и нормализуем в единый формат
const out = [];
for (const item of items) {
  const n = normalize(item.json);
  const text = cleanText(n.text);
  const pages = Array.isArray(n.pages)
    ? n.pages.map((p) => ({
        pageNumber: p.pageNumber ?? null,
        text: cleanText(p.text ?? ''),
        textLength: typeof p.text === 'string' ? p.text.length : 0,
        imageFile: p.imageFile ?? null,
        error: p.error ?? undefined,
      }))
    : [];

  out.push({
    json: {
      success: true,
      source: 'ocr',
      jobId: n.jobId,
      status: n.status,
      fileName: n.fileName,
      text,
      pages,
      pageCount: pages.length,
    },
  });
}
return out;

const out = [];

for (const item of items) {
  const pages = Array.isArray(item.json?.pages) ? item.json.pages : [];

  if (pages.length === 0) {
    const t = item.json?.text ?? '';
    out.push({
      json: {
        pageNumber: 1,
        text: t,
        textLength: typeof t === 'string' ? t.length : 0,
        jobId: item.json?.jobId,
        fileName: item.json?.fileName,
      },
    });
    continue;
  }

  let i = 0;
  for (const p of pages) {
    i += 1;
    const t = p?.text ?? '';
    out.push({
      json: {
        pageNumber: p?.pageNumber ?? i,
        text: t,
        textLength: typeof t === 'string' ? t.length : 0,
        jobId: item.json?.jobId,
        fileName: item.json?.fileName,
      },
    });
  }
}

return out;
