// Generate UUID without crypto module using timestamp and random numbers
function generateUUID() {
  const timestamp = Date.now().toString(16);
  const random1 = Math.random().toString(16).substring(2, 10);
  const random2 = Math.random().toString(16).substring(2, 10);
  const random3 = Math.random().toString(16).substring(2, 10);

  return `${timestamp.substring(0, 8)}-${random1.substring(0, 4)}-${random1.substring(4, 8)}-${random2.substring(0, 4)}-${random2.substring(4, 8)}${random3.substring(0, 4)}`;
}

const uuid = generateUUID();

// Return as item data
return [
  {
    json: { uuid },
  },
];

const aiOutput = $input.first().json.output;
let tasksArray;

try {
  // Remove markdown code blocks if present
  const cleanOutput = aiOutput.replace(/```json\n?|```\n?/g, '').trim();
  tasksArray = JSON.parse(cleanOutput);

  // Ensure it's an array
  if (!Array.isArray(tasksArray)) {
    tasksArray = [tasksArray];
  }
} catch (error) {
  throw new Error('Failed to parse AI Agent output: ' + error.message);
}

// Return each task as a separate item with UUID, all fields plus created_at timestamp
return tasksArray.map(task => ({
  json: {
    owner_tg_id: task.owner_tg_id,
    owner_name: task.owner_name,
    assignee_tg_id: task.assignee_tg_id,
    assignee_name: task.assignee_name,
    task_description: task.task_description,
    deadline: task.deadline,
    deadline_source: task.deadline_source,
    created_at: new Date(),
    Status:task.status

  }
}));

const raw =  $input.first().json.callback_query.data;
const [uuid,status,message_id] = raw.split('|');

return [{
  json: {
    uuid, 
  status,
  message_id}
}];

